mapper.select:
SELECT id,name,number,introducation,coordinator,email,telephone,grade_name FROM tb_clazz
AND name = #{name}
AND name LIKE concat(concat('%',#{grade_name}),'%')

mapper.update:
UPDATE tb_clazz
name=#{name}, number=#{number}, introducation=#{introducation}, coordinator=#{coordinator}, email=#{email}, telephone=#{telephone}, grade_name=#{grade_name},
WHERE id = #{id}
